mapper.select:
SELECT M.ID, M.NAME, M.PHONE, M.GENDER, M.BIRTH, T.TRAINER_INTRO, T.CAREER, T.QUESTION_FIRST, T.QUESTION_SECOND, T.REGISTER_DATE   
FROM MEMBER M, TRAINER_REGISTER T 
WHERE M.ID = T.TRAINER_ID AND ID = #{id}

mapper.update:
UPDATE TRAINER_REGISTER 
SET REG_STATUS = 'RS01' 
WHERE TRAINER_ID = #{id}

UPDATE TRAINER_REGISTER 
SET REG_STATUS = 'RS02' 
WHERE TRAINER_ID = #{id}

mapper.select:
SELECT M.NAME ,M.ID, M.GENDER, M.BIRTH , C.COMMON_NAME FROM MEMBER M, TRAINER_REGISTER T, COMMON C WHERE M.ID = T.TRAINER_ID AND T.REG_STATUS = C.COMMON_CODE
AND T.REG_STATUS = #{value}
ORDER BY T.REG_STATUS,T.REGISTER_DATE

mapper.update:
UPDATE FIT_CLASS 
SET CLS_STATUS = #{btnId} 
WHERE CLS_CODE= #{id}

mapper.select:
SELECT M.NAME, C.COMMON_NAME,F.CLS_NAME,TO_CHAR(F.START_DATE,'YYYY-MM-DD') AS START_DATE,TO_CHAR(F.END_DATE,'YYYY-MM-DD') AS END_DATE ,F.YOIL,F.CLS_STATUS, F.CLS_CODE FROM FIT_CLASS F, COMMON C, MEMBER M WHERE F.CLS_CATEGORY = C.COMMON_CODE AND F.TRAINER_ID = M.ID
AND F.CLS_STATUS = #{value}
ORDER BY F.CLS_STATUS,START_DATE

SELECT C.COMMON_NAME, F.TRAINER_ID, F.CLS_NAME, P.MEM_ID, M.GENDER, M.BIRTH, P.PAY_DATE FROM PAYMENT P ,FIT_CLASS F, COMMON C, MEMBER M WHERE PAY_DATE BETWEEN TO_DATE('20210101','YYYYMMDD') AND SYSDATE AND P.CLS_CODE = F.CLS_CODE AND F.CLS_CATEGORY = C.COMMON_CODE AND P.MEM_ID = M.ID
AND C.COMMON_NAME= #{value}
ORDER BY P.PAY_DATE DESC

SELECT C.CDATE , NVL(D.PCOUNT,0) AS PCOUNT FROM (SELECT TO_CHAR(TO_DATE('20210401','YYYYMMDD') + (INTERVAL '1' DAY)*(LEVEL-1) ,'MM/DD' ) AS CDATE FROM DUAL
CONNECT BY LEVEL <= (SELECT TO_CHAR(LAST_DAY('20210401'),'DD')
FROM DUAL)) C LEFT JOIN (SELECT TO_CHAR(JOIN_DATE,'MM/DD') AS CDATE , COUNT(*) AS PCOUNT FROM MEMBER WHERE TO_CHAR(JOIN_DATE,'YYYY/MM') = '2021/04' AND ROLE='RL00' AND WITHDRAWAL = 'WD00' GROUP BY TO_CHAR(JOIN_DATE,'MM/DD')) D ON C.CDATE = D.CDATE ORDER BY C.CDATE;

SELECT C.CDATE , NVL(D.PCOUNT,0) AS PCOUNT FROM (SELECT TO_CHAR(TO_DATE('20210401','YYYYMMDD') + (INTERVAL '1' DAY)*(LEVEL-1) ,'MM/DD' ) AS CDATE FROM DUAL
CONNECT BY LEVEL <= (SELECT TO_CHAR(LAST_DAY('20210401'),'DD')
FROM DUAL)) C LEFT JOIN (SELECT TO_CHAR(JOIN_DATE,'MM/DD') AS CDATE , COUNT(*) AS PCOUNT FROM MEMBER WHERE TO_CHAR(JOIN_DATE,'YYYY/MM') = '2021/04' AND ROLE='RL01' AND WITHDRAWAL = 'WD00' GROUP BY TO_CHAR(JOIN_DATE,'MM/DD')) D ON C.CDATE = D.CDATE ORDER BY C.CDATE;
