mapper.select:
SELECT * FROM MEMBER
WHERE ID = #{id}
AND PASSWORD = #{password}
AND WITHDRAWAL = 'WD00'

		SELECT *
		FROM MEMBER m, TRAINER_REGISTER t
        WHERE m.ID = t.TRAINER_ID
		AND m.ID = #{id}
		AND m.PASSWORD = #{password}
		AND t.REG_STATUS = 'RS01'

	SELECT TO_CHAR(TOTAL_CAL, '999,999,999') AS TOTAL_CAL
    FROM TRAINER_REGISTER
    WHERE TRAINER_ID = #{id}

mapper.update:
		UPDATE MEMBER
    	SET PASSWORD = #{password}
    	WHERE ID = #{id}

		UPDATE TRAINER_REGISTER
    	SET TRAINER_INTRO = #{trainerIntro}, 
        CAREER = #{career}
    	WHERE TRAINER_ID = #{id}

mapper.select:
SELECT TO_CHAR(f.END_DATE + 1, 'YYYY-MM-DD') as endDate, f.CLS_NAME, TO_CHAR(NVL(p.PAID_PRICE, 0), '999,999,999') as paidPrice, TO_CHAR(NVL(p.COMMISSION, 0), '999,999,999') as commission, TO_CHAR(NVL(p.NET_PRICE, 0), '999,999,999') as netPrice FROM TRAINER_REGISTER t, FIT_CLASS f, PAYMENT P WHERE t.TRAINER_ID = f.TRAINER_ID AND f.CLS_CODE = p.CLS_CODE AND t.TRAINER_ID = #{id}
AND TO_CHAR(f.END_DATE + 1, 'YYYY-MM-DD') >= #{sDate} AND TO_CHAR(f.END_DATE + 1, 'YYYY-MM-DD') <= #{eDate}
ORDER BY endDate DESC
